                <option value="令和">令和</option>
              </select>
              <input 
                type="text" 
                value={formData.transferOutDate.year} 
                onChange={(e) => handleDateChange('transferOutDate', 'year', e.target.value)}
                className="form-control year-input"
                style={{ ...inputStyle, width: '40px' }}
                readOnly={!isEditing}
                placeholder=""
              />
              <span>年</span>
              <input 
                type="text" 
                value={formData.transferOutDate.month} 
                onChange={(e) => handleDateChange('transferOutDate', 'month', e.target.value)}
                className="form-control month-input"
                style={{ ...inputStyle, width: '40px' }}
                readOnly={!isEditing}
                placeholder=""
              />
              <span>月</span>
              <input 
                type="text" 
                value={formData.transferOutDate.day} 
                onChange={(e) => handleDateChange('transferOutDate', 'day', e.target.value)}
                className="form-control day-input"
                style={{ ...inputStyle, width: '40px' }}
                readOnly={!isEditing}
                placeholder=""
              />
              <span>日</span>
            </div>
          </div>
        </div>
        <div className="form-row">
          <div className="form-group">
            <label>転出先の事業所名等</label>
            <input 
              type="text" 
              name="nextWorkplace" 
              value={formData.nextWorkplace} 
              onChange={handleChange}
              className="form-control"
              placeholder="(該当なし)"
              style={inputStyle}
              readOnly={!isEditing}
            />
          </div>
        </div>
      </div>

      {/* 勤務条件 */}
      <div className="work-conditions">
        <h3 className="section-title">勤務条件</h3>
        <div className="form-row">
          <div className="form-group">
            <label>勤務時間</label>
            <input 
              type="text" 
              name="workHours" 
              value={formData.workHours} 
              onChange={handleChange}
              className="form-control"
              style={inputStyle}
              readOnly={!isEditing}
            />
          </div>
          <div className="form-group">
            <label>休憩時間</label>
            <input 
              type="text" 
              name="breakTime" 
              value={formData.breakTime} 
              onChange={handleChange}
              className="form-control"
              style={inputStyle}
              readOnly={!isEditing}
            />
          </div>
        </div>
        <div className="form-row">
          <div className="form-group">
            <label>労働時間</label>
            <input 
              type="text" 
              name="workingHours" 
              value={formData.workingHours} 
              onChange={handleChange}
              className="form-control"
              style={inputStyle}
              readOnly={!isEditing}
            />
          </div>
          <div className="form-group">
            <label>週勤務日数</label>
            <select 
              name="workDaysPerWeek" 
              value={formData.workDaysPerWeek} 
              onChange={handleChange}
              className="form-control"
              style={inputStyle}
              disabled={!isEditing}
            >
              <option value="5日">5日</option>
              <option value="4日">4日</option>
              <option value="3日">3日</option>
              <option value="2日">2日</option>
              <option value="1日">1日</option>
            </select>
          </div>
        </div>
      </div>
    </div>
  );
};

